import "./ExpenseForm.css";

import React, {useState} from 'react';

const ExpenseForm = () => {
    // const [enteredNumber, setEnteredNumber] = useState('');
    // const [enteredTitle, setEnteredTitle] = useState('');
    // const [enteredDate, setEnteredDate] = useState('');

    // passing one object instead of having three states
    const[userInput, setUserInput]  = useState({
        enteredTitle: '',
        enteredNumber: '',
        enteredDate:  '',
    });
    
    const titleChangeHandler = (event) => {
        // setEnteredTitle(event.target.value);
        setUserInput({
            ...userInput,
            enteredTitle: event.target.value,
           
        })
    }

    const numberChangeHandler = (event) => {
        // setEnteredNumber(event.target.value);
        setUserInput({
            ...userInput,
            enteredNumber: event.target.value,
           
        })
       
    }

    const dateChangeHandler = (event) => {
        // setEnteredDate(event.target.value);
        setUserInput({
            ...userInput,
            enteredTitle: event.target.value,
           
        })
       
    }
  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" min="0.01" step="0.01" onChange={numberChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input type="date" min="2023-01-01" max="2024-12-31" onChange={dateChangeHandler} />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
